package com.sudnu.pluginimport android.app.Activityimport android.os.Bundleimport android.view.LayoutInflaterimport android.view.Viewimport com.sudnu.plugin_base.plugin_apk.IPluginProxyActivityimport com.sudnu.plugin_base.plugin_apk.PluginLoaderclass PluginActivity : IPluginProxyActivity {    private lateinit var rootView: View    override fun onCreate(activity: Activity, savedInstanceState: Bundle?) {        var resourceId = R.layout.layout        var xmlPare = PluginLoader.pluginResources?.getLayout(resourceId)        rootView = LayoutInflater.from(activity).inflate(xmlPare,null)    }    override fun onStart() {    }    override fun onRestart() {    }    override fun onResume() {    }    override fun onPause() {    }    override fun onStop() {    }    override fun onDestroy() {    }    override fun getRootView(): View {        return rootView    }}