package com.sudnu.pluginveiwimport android.content.Contextimport android.view.Viewimport android.widget.Toastimport com.sudnu.layout_dsl.dsl.*import com.sudnu.plugin_base.plugin_view.IPluginViewimport com.sudnu.plugin_base.plugin_view.PluginLoadAarimport com.sudnu.pluginveiw.Rclass MyPluginView: IPluginView {    override fun loadView(context: Context): View? {        context?.apply {           return ConstraintLayout {                layout_width = match_parent                layout_height = match_parent                TextView() {                    layout_id = "tvCommit"                    layout_width = wrap_content                    layout_height = wrap_content                    text = "commit"                    gravity = gravity_center                    textStyle = bold                    align_vertical_to = "ivBack"                    center_horizontal = true                    padding = 10                    onClick = { _: View ->                        Toast.makeText(context, "on click cc", Toast.LENGTH_LONG).show()                    }                    shape = shape {                        corner_radius = 10                        shape = shape_rectangle                        gradientType = gradient_type_linear                        orientation = gradient_left_right                        gradient_colors = listOf("#ffff00", "#0000ff")                        strokeAttr = Stroke(5, "#000000", dashWidth = 2f, dashGap = 3f)                        color_state_list = listOf(                            intArrayOf(state_enable) to "#007EFF",                            intArrayOf(state_disable) to "#FDB2DA"                        )                    }                }              ImageView {                   layout_id = "tvImageView"                   layout_width = wrap_content                   layout_height = wrap_content                   top_toBottomOf = "tvCommit"                   srcPlugin = PluginLoadAar.pluginResources?.getDrawable(R.mipmap.ic_launcher)               }            }        }        return null    }}